/*Table structure for table `ldh_lu_organize` */

DROP TABLE IF EXISTS `ldh_lu_organize`;

CREATE TABLE `ldh_lu_organize` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `orderid` int(11) DEFAULT NULL,
  `uname` varchar(100) DEFAULT NULL,
  `product` varchar(255) DEFAULT NULL,
  `tel` varchar(100) DEFAULT NULL,
  `qq` varchar(150) NOT NULL,
  `pay` varchar(150) NOT NULL,
  `number` int(2) DEFAULT NULL,
  `sid` int(11) unsigned DEFAULT NULL COMMENT '学生ID',
  `dist` varchar(100) DEFAULT NULL,
  `city` varchar(100) DEFAULT NULL,
