  `r_time` int(11) unsigned DEFAULT NULL COMMENT '成为党员时间',
  `c_time` int(11) unsigned DEFAULT NULL COMMENT '组织关系转出时间',
  `jsr_1` varchar(20) NOT NULL COMMENT '介绍人一',
  `jsr_2` varchar(20) NOT NULL COMMENT '介绍人二',
  `status` varchar(50) NOT NULL COMMENT '状态',
  `create_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  `ip` varchar(255) DEFAULT NULL,
  `remark` varchar(500) NOT NULL COMMENT '备注',
  PRIMARY KEY (`id`),
  UNIQUE KEY `sid` (`sid`)
